function scrollingDropdown_onclick(b,g){var e=0;
var f="";
var d=document.getElementById(b.toString());
var a=d.getElementsByTagName("input");
if(a.length==1&&a[0].disabled){f="No "+g.toString()+"s to select."
}else{for(var c=0;
c<a.length;
c++){if(a[c].checked){e++;
f=a[c].parentNode.childNodes[1].innerHTML
}if(e>1){f="Multiple "+g.toString()+"s selected"
}if(a[0].checked){f=a[0].parentNode.childNodes[1].innerHTML
}if(e===0){f="Please Choose "+g.toString()+"(s)"
}}f=DecodeString(f);
if(f.length>32){f=f.substr(0,30)+".."
}d.parentNode.children[1].children[0].firstChild.nodeValue=f
}};
