function scrollingDropdown_onclick(b,h,d){var f=0;
var g="";
if(isNaN(d)){d="s"
}var e=document.getElementById(b.toString());
var a=e.getElementsByTagName("input");
if(a.length==1&&a[0].disabled){g="No "+h.toString()+"s to select."
}else{for(var c=0;
c<a.length;
c++){if(a[c].checked){f++;
g=a[c].parentNode.childNodes[1].innerHTML
}if(f>1){g="Multiple "+h.toString()+"s selected"
}if(a[0].checked){g=a[0].parentNode.childNodes[1].innerHTML
}if(f===0){g="Please Choose "+h.toString()+"("+d+")"
}}g=DecodeString(g);
if(g.length>32){g=g.substr(0,30)+".."
}e.parentNode.children[1].children[0].firstChild.nodeValue=g
}};
